parameters.jsCode:
// 1. n8n의 입력 데이터를 가져옵니다.
const items = $input.all();

// 2. 실제 텍스트 데이터가 있는 경로로 접근합니다.
const rawText = items[0]?.json?.content?.parts?.[0]?.text;

// 데이터가 없으면 빈 결과를 반환
if (!rawText) return [];

// 3. 정규 표현식을 사용하여 ```json ... ``` 사이의 내용만 추출합니다.
const jsonMatch = rawText.match(/```json\n([\s\S]*?)\n```/m);
if (!jsonMatch || jsonMatch.length < 2) return [];

// 4. JSON 객체로 변환
const jsonString = jsonMatch[1].trim();
let parsedData;

try {
    parsedData = JSON.parse(jsonString);
} catch (e) {
    return [{ json: { error: "JSON 파싱 실패" } }];
}

// 5. [핵심] 모든 데이터를 평탄화(Flat)하여 결과 객체 생성
const result = {};

// --- (A) Meta 정보 ---
if (parsedData.meta) {
    result.applicant_name = parsedData.meta.applicant_name;
    result.target_position = parsedData.meta.target_position;
    result.date = parsedData.meta.date;
}

// --- (B) Executive Summary ---
if (parsedData.executive_summary) {
    result.exec_headline = parsedData.executive_summary.headline;
    result.exec_summary_text = parsedData.executive_summary.summary_text;
    result.exec_key_strategy = parsedData.executive_summary.key_strategy;
    result.exec_strong_point = parsedData.executive_summary.strong_point;
    result.exec_weak_point = parsedData.executive_summary.weak_point;
    result.exec_synergy_point = parsedData.executive_summary.synergy_point;
}

// --- (C) Sections: Self Introduction ---
if (parsedData.sections?.self_introduction) {
    result.self_intro_analysis = parsedData.sections.self_introduction.analysis;
    result.self_intro_strategy = parsedData.sections.self_introduction.strategy;
    result.self_intro_tobe_copy = parsedData.sections.self_introduction.tobe_copy;
}

// --- (D) Sections: Core Competencies ---
if (parsedData.sections?.core_competencies) {
    result.competency_analysis = parsedData.sections.core_competencies.analysis;
    result.competency_strategy = parsedData.sections.core_competencies.strategy;
    result.competency_tobe_structure = parsedData.sections.core_competencies.tobe_structure;
}

// --- (E) Sections: Tech Stack ---
if (parsedData.sections?.tech_stack) {
    result.tech_analysis = parsedData.sections.tech_stack.analysis;
    result.tech_strategy = parsedData.sections.tech_stack.strategy;
    result.tech_tobe_structure = parsedData.sections.tech_stack.tobe_structure;
}

// --- (F) Sections: Key Achievements (배열 자동 처리) ---
// 예: achievement_1_title, achievement_2_title ... 식으로 생성됨
if (Array.isArray(parsedData.sections?.key_achievements)) {
    parsedData.sections.key_achievements.forEach((item, index) => {
        const num = index + 1; // 1부터 시작
        result[`achievement_${num}_title`] = item.title;
        result[`achievement_${num}_asis`] = item.asis;
        result[`achievement_${num}_tobe`] = item.tobe;
    });
}

// --- (G) Sections: Career History (배열 자동 처리) ---
// 예: career_1_company_name, career_2_company_name ... 식으로 생성됨
if (Array.isArray(parsedData.sections?.career_history)) {
    parsedData.sections.career_history.forEach((item, index) => {
        const num = index + 1;
        result[`career_${num}_company_name`] = item.company_name;
        result[`career_${num}_company_role`] = item.company_role;
        result[`career_${num}_company_period`] = item.company_period;
        result[`career_${num}_tobe_bullets`] = item.tobe_bullets;
    });
}

// --- (H) Final Checklist ---
result.final_checklist = parsedData.final_checklist;

// 6. 결과 반환
return [{ json: result }];

