    // 1. LLM ÏùëÎãµ ÌÖçÏä§Ìä∏ Ï∂îÏ∂ú
    // Ï†úÍ≥µÌï¥Ï£ºÏã† Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞: [{ content: { parts: [{ text: "..." }] } }]
    const textResponse = item.json.content.parts[0].text;

    if (!textResponse) {
      throw new Error("LLM ÏùëÎãµÏóêÏÑú ÌÖçÏä§Ìä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
    }

    // 2. JSON Î¨∏ÏûêÏó¥ Ï∂îÏ∂ú (Data Cleaning)
    // LLMÏù¥ "```json { ... } ```" Ï≤òÎüº ÎßàÌÅ¨Îã§Ïö¥ÏùÑ Ìè¨Ìï®ÌïòÍ±∞ÎÇò, ÏïûÎí§Ïóê ÏÇ¨Ï°±ÏùÑ Î∂ôÏùº Í≤ΩÏö∞Î•º ÎåÄÎπÑÌï©ÎãàÎã§.
    // Í∞ÄÏû• Ï≤òÏùå ÎÇòÏò§Îäî '{' Î∂ÄÌÑ∞ Í∞ÄÏû• ÎßàÏßÄÎßâÏóê ÎÇòÏò§Îäî '}' ÍπåÏßÄ ÏûòÎùºÎÉÖÎãàÎã§.
    const firstOpenBrace = textResponse.indexOf('{');
    const lastCloseBrace = textResponse.lastIndexOf('}');

    if (firstOpenBrace === -1 || lastCloseBrace === -1) {
      throw new Error("ÏùëÎãµ ÌÖçÏä§Ìä∏ ÎÇ¥ÏóêÏÑú Ïú†Ìö®Ìïú JSON Íµ¨Ï°∞({ ... })Î•º Ï∞æÏßÄ Î™ªÌñàÏäµÎãàÎã§.");
    }

    const cleanJsonString = textResponse.substring(firstOpenBrace, lastCloseBrace + 1);

    // 3. JSON ÌååÏã± (String -> Object Î≥ÄÌôò)
    const parsedResumeData = JSON.parse(cleanJsonString);

    // 4. Í≤∞Í≥º Î∞òÌôò
    // item.jsonÏùÑ ÌååÏã±Îêú Ïù¥Î†•ÏÑú Îç∞Ïù¥ÌÑ∞Î°ú ÎçÆÏñ¥ÏîåÏõÅÎãàÎã§.
    item.json = parsedResumeData;

    return item;

  } catch (error) {
    // ÏóêÎü¨ Î∞úÏÉù Ïãú, ÏóêÎü¨ ÎÇ¥Ïö©Í≥º ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞Î•º Ìï®Íªò Ï∂úÎ†•ÌïòÏó¨ ÎîîÎ≤ÑÍπÖÏùÑ ÎèïÏäµÎãàÎã§.
    item.json = {
      error: true,
      message: error.message,
      rawInput: item.json // ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞ Î≥¥Ï°¥
    };
    return item;
  }
});
